import requestsOWM_ENDPOINT = "https://api.openweathermap.org/data/2.5/forecast"MY_LAT = 41.342670MY_LONG = -8.477460MY_API_KEY = ""parameters={    "lat": MY_LAT,    "lon":MY_LONG,    "appid":MY_API_KEY,    "cnt":4,}umbrella_needed = Falseresponse = requests.get(url = OWM_ENDPOINT, params=parameters)response.raise_for_status()weather = response.json()weather_list = weather["list"]time_weather = 0time_id = 0condition_code = []for time in weather_list:    weather_list_now = weather_list[time_weather]["weather"]    time_weather += 1    for weather_in in weather_list_now:        weather_id = weather_list_now[time_id]["id"]        condition_code.append(weather_id)        if len(weather_list_now) > 1:            time_id += 1        time_id=0for value in condition_code:    if value <700:        umbrella_needed = Trueif umbrella_needed:    print("Bring an umbrella")